<div class="container"  ng-controller="loginController">    <div class="col-xs-12 col-md-12 col-sm-12 col-lg-12">        <div class="alert alert-warning">            <h2>You entered incorrect username or password</h2>        </div>        <div class="col-md-4"></div>        <div class="col-md-4">            <form class="form-horizontal" role="form"  ng-submit="loginAdmin();">                <div class="form-group">                    <label for="inputEmail3" class="col-sm-2 control-label">Email</label>                    <div class="col-sm-10">                        <input type="email" class="form-control" name="email"  ng-model="email" id="inputEmail3" placeholder="Email">                    </div>                </div>                <div class="form-group">                    <label for="inputPassword3" class="col-sm-2 control-label">Password</label>                    <div class="col-sm-10">                        <input type="password" name="password" class="form-control"  ng-model="password" id="inputPassword3" placeholder="Password">                    </div>                </div>                <div class="form-group">                    <div class="col-sm-offset-2 col-sm-10">                        <button type="submit" class="btn btn-success">Sign in</button>                    </div>                </div>            </form>        </div>        <div class="col-md-4"></div>    </div></div><script>    var loginController = function ($scope, $http) {        var serviceUrlPost = '<?php echo base_url('api/login/index');?>';        $scope.loginAdmin = function () {            var data1 = {                email: $scope.email,                password: $scope.password,                apikey: '<?=$this->config->item('hitech_apikey')?>',            };            $http.post(serviceUrlPost, data1).success(function (data, status, headers, config) {                location.href = '<?php echo base_url('admin/customers');?>';            }).error(function (data, status, headers, config) {                $(".alert-warning").html(data.error);                $(".alert-warning").show();            });        };        $(".alert-warning").hide();    };</script>